import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { Carros } from '../carros';
import { CarrosService } from '../carros.service';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';

@Component({
  selector: 'app-confirmar-interesse',
  templateUrl: './confirmar-interesse.component.html',
  styleUrls: ['./confirmar-interesse.component.css']
})

  export class ConfirmarInteresseComponent implements OnInit {
    cliente: any = {}; // Declare a propriedade cliente aqui
    carroInteressado: Carros | undefined; // Declare a propriedade carroInteressado com o tipo Carros

    constructor(
      private route: ActivatedRoute,
      private router: Router,
      private clienteService: ClienteService
    ) {}

    ngOnInit(): void {
      // Obtenha os detalhes do carro interessado da rota
      this.carroInteressado = this.route.snapshot.data['carroInteressado'];

      // Inicialize a propriedade cliente com o objeto Carros correspondente
      this.cliente = {
        nome: '',
        telefone: null,
        dataInteresse: new Date(),
        carroInteressado: this.carroInteressado,
      };
    }

    onSubmit(): void {
      // Adicione a lógica para lidar com o envio do formulário aqui
      this.clienteService.registrarReserva(this.cliente).subscribe(
        () => {
          // Lógica para tratamento de sucesso
          this.router.navigate(['/carros-reservados']);
        },
        (error) => {
          console.error('Erro ao registrar reserva:', error);
        }
      );
    }
  }
