import { Component, OnInit } from '@angular/core';
import { Militar } from '../militar';
import { MilitarService } from '../militar.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-militar-list',
  templateUrl: './militar-list.component.html',
  styleUrls: ['./militar-list.component.css']
})
export class MilitarListComponent implements OnInit {

 militars: Militar[] = [];

  constructor(private militarService: MilitarService,
    private router: Router) { }


  ngOnInit(): void {
    this.getMilitars();


   }

  private getMilitars(){
    this.militarService.getMilitarsList().subscribe(data => {
      this.militars = data;
    });
  }

  militarDetails(id: number){
    this.router.navigate(['militar-details', id] );
  }

  updateMilitar(id: number){
    this.router.navigate(['update-militar', id] );
  }

  deleteMilitar(id: number){
    this.militarService.deleteMilitar(id).subscribe( data =>{
      console.log(data);
      this.getMilitars();
    })
  }

}
