import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { Usuario } from '../usuario';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  usuario: any = {};

  constructor(private authService: AuthService, private router: Router) {}

  login() {
    this.authService.login(this.usuario).subscribe(
      response => {
        // Lógica após o login bem-sucedido
        console.log(response);




        this.router.navigate(['/home']);


      },
      error => {
        // Lógica de tratamento de erro
        console.error(error);

      }
    );
  }
}


<div class="col-md-6 offset-md-3">

    <h3 class="cor">  </h3>
    <form (ngSubmit) = "login()">

        <div class="form-group">
            <label class="cor"> Login: </label>
            <input type="text" class ="form-control" id = "login"
                [(ngModel)] = "usuario.login" name = "login">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Senha: </label>
            <input type="password" class ="form-control" id = "senha"
                [(ngModel)] = "usuario.senha" name = "senha">
        </div>

        <div> </div>
        <button (click) = "login" class = "btn btn-success"  >Acessar</button>

        <button (click) = "login" class = "btn btn-success"  >Recuperar Senha</button>

    </form>
    </div>
