import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Militar } from '../militar';
import { MilitarService } from '../militar.service';

@Component({
  selector: 'app-details-militar',
  templateUrl: './details-militar.component.html',
  styleUrls: ['./details-militar.component.css']
})
export class MilitarDetailsComponent implements OnInit {

  id!: number;
  militar!: Militar;
  constructor(private route: ActivatedRoute, private militarService: MilitarService) { }

   ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];

     this.militar = new Militar();
     this.militarService.getMilitarByid(this.id).subscribe( data => {
       this.militar = data;
     })
 }
 }
