import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Usuario } from './usuario';
import { Carros } from './carros';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

  export class AppComponent {
    title = 'frontend';
    id!: number;
    carro: Carros = new Carros();

    constructor(private router: Router, private route: ActivatedRoute) {}

    isLoginOuAlterarSenha(): boolean {
      const currentUrl = this.router.url;

      // Obtém o valor do parâmetro da rota (ID do carro)
      const carId = this.route.snapshot.paramMap.get('id');

      // Verifica se a URL é exatamente http://localhost:4200/carros-details-client/{id}
      if (currentUrl === `pi/carros-details-client/${carId}`) {
        return false;
      }

      // Caso contrário, continuar exibindo o menu
      return (
        currentUrl === '/login' ||
        currentUrl === '/alterar-senha' ||
        currentUrl === '/carros-list-client'
      );
    }
  }

<body class="bg">
<nav  class="nav" class = "navbar navbar-expand-sm bg-dark navbar-dark" *ngIf="!isLoginOuAlterarSenha()" >
    <ul class = "navbar-nav">
        <li class = "nav-item">
            <a href="home/" routerlink="/" routerLinkActive="active" class="nav-link">

              Início</a>
          </li>
    <li class = "nav-item">
      <a href="usuario-list" routerlink="/usuario-list" routerLinkActive="active" class="nav-link">

        Lista de Usuarios</a>
    </li>
    <li class="nav-item">
      <a href="create-usuario" routerlink="/create-usuario" routerLinkActive="active" class="nav-link">Criar Usuario</a>
    </li>
    <li class = "nav-item">
        <a href="carros-list" routerlink="/carros-list" routerLinkActive="active" class="nav-link">

          Lista de Carros</a>
      </li>
      <li class="nav-item">
        <a href="create-carros" routerlink="/create-carros" routerLinkActive="active" class="nav-link">Cadastrar Carros</a>
      </li>
      <li class="nav-item">
      <a href="login/" routerlink="/login" routerLinkActive="active" class="nav-link">

        Sair</a>
    </li>



   <li class="nav-item">
    <a class="nav-link"> |   Projeto Integrador</a>
   </li>

    </ul>

    </nav>

<router-outlet></router-outlet>
</body>
