import { Component, OnInit } from '@angular/core';
import { Militar } from '../militar';
import { MilitarService } from '../militar.service';
import { Router } from '@angular/router';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-create-militar',
  templateUrl: './create-militar.component.html',
  styleUrls: ['./create-militar.component.css']
})
export class CreateMilitarComponent implements OnInit {

 militar = {
  cpf: '',
 };
 errorMessage ='';
  http: any;

  constructor (private militarService: MilitarService,
    private router: Router, http: HttpClient) { }

    createMilitar() {
      const url = 'http://localhost:8080/api/vfinal/militar';

      const headers = new HttpHeaders({
        'Content-Type': 'application/json'
      });

      this.http.post(url, this.militar, { headers })
      .subscribe(
        (        response: any) => {
          console.log('Sucesso: ', response);
        },
        (        error: { error: string; }) => {
          console.error('Erro:', error);
          this.errorMessage = error.error;
        }
      )
    }


  ngOnInit(): void { }





}
<div class="col-md-6 offset-md-3">
    <h3 class="cor"> Cadastrar Militar </h3>
    <form ngSubmit = "onSubmit()">

        <div class="form-group">
            <label class="cor"> Nome: </label>
            <input type="text" class ="form-control" id = "nome"
                (ngModel)] = "militar.nome" name = "nome">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> CPF: </label>
            <input type="text" class ="form-control" id = "CPF"
                (ngModel)] = "militar.CPF" name = "CPF">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Sobrenome: </label>
            <input type="text" class ="form-control" id = "sobrenome"
                (ngModel)] = "militar.sobrenome" name = "sobrenome">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Tipo Sanguíneo: </label>
            <input type="text" class ="form-control" id = "tipoSanguineo"
                (ngModel)] = "militar.tipoSanguineo" name = "tipoSanguineo">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Patente: </label>
            <input type="text" class ="form-control" id = "patente"
                (ngModel)] = "militar.patente" name = "patente">
        </div>
        <div> </div>
        <button class = "btn btn-success" (click)="createMilitar()">Cadastrar Militar</button>



    </form>
    <div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>
    </div>
