import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { Carros } from '../carros';
import { CarrosService } from '../carros.service';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';
import { DadosCarrosService } from '../dados-carros.service';

@Component({
  selector: 'app-confirmar-interesse',
  templateUrl: './confirmar-interesse.component.html',
  styleUrls: ['./confirmar-interesse.component.css']
})

export class ConfirmarReservaComponent implements OnInit {
  cliente: any = {}; // Certifique-se de ajustar conforme seu modelo Cliente
  carroSelecionado: any;

  constructor(private dadosCarros: DadosCarrosService) {}

  ngOnInit(): void {
    // Assine as alterações no carro selecionado
    this.dadosCarros.carroSelecionado$.subscribe(carro => {
      this.carroSelecionado = carro;
    });
  }

  onSubmit(): void {
    // Adicione lógica para confirmar reserva usando this.cliente e this.carroSelecionado
  }
}
