import { Endereco } from './../endereco';
import { Component, OnInit } from '@angular/core';
import { EnderecoService } from '../endereco.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-endereco',
  templateUrl: './create-endereco.component.html',
  styleUrls: ['./create-endereco.component.css']
})
export class CreateEnderecoComponent implements OnInit {

 endereco: Endereco = new Endereco();

  constructor (private enderecoService: EnderecoService,
    private router: Router) { }


  ngOnInit(): void { }

    saveEndereco() {
      this.enderecoService.createEndereco(this.endereco).subscribe(data =>{

      })
      this.router.navigate(['/enderecos-list'])
    }

    cancel(): void {
      this.router.navigate(['/endereco-list']);
    }

    onSubmit(){
      console.log(this.endereco);
      this.saveEndereco();
    }
}


<div class="col-md-6 offset-md-3">

    <h3 class="cor"> Cadastrar Endereço </h3>
    <form (ngSubmit) = "onSubmit()">

        <div class="form-group">
            <label class="cor"> Rua: </label>
            <input type="text" class ="form-control" id = "rua"
                [(ngModel)] = "endereco.rua" name = "rua">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Número: </label>
            <input type="text" class ="form-control" id = "numero"
                [(ngModel)] = "endereco.numero" name = "numero">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor">  Cidade: </label>
            <input type="text" class ="form-control" id = "cidade"
                [(ngModel)] = "endereco.cidade" name = "cidade">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Estado: </label>
            <input type="text" class ="form-control" id = "estado"
                [(ngModel)] = "endereco.estado" name = "estado">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> CEP: </label>
            <input type="text" class ="form-control" id = "cep"
                [(ngModel)] = "endereco.cep" name = "cep">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Complemento: </label>
            <input type="text" class ="form-control" id = "complemento"
                [(ngModel)] = "endereco.complemento" name = "complemento">
        </div>
        <div> </div>
        <button class = "btn btn-success" (click)="saveEndereco()"  >Cadastrar Endereço</button>



    </form>
    </div>
