import { Router } from '@angular/router';
import { Component } from '@angular/core';
import { AlterarSenhaService } from '../alterar-senha.service';
import { Usuario } from '../usuario';

@Component({
  selector: 'app-alterar-senha',
  templateUrl: './alterar-senha.component.html',
  styleUrls: ['./alterar-senha.component.css']
})
export class AlterarSenhaComponent {
  usuario: Usuario = new Usuario();
  NovaSenha: string | undefined;
  mensagem: string | undefined;

  constructor(private alterarSenhaService: AlterarSenhaService) {}

  alterarSenha() {
    // Adicione a lógica para chamar o serviço de alteração de senha aqui
    const request: any = {
      login: this.usuario.login,
      dataNascimento: this.usuario.dataNascimento,
      senha: this.usuario.senha,
      novaSenha: this.NovaSenha
    };

    this.alterarSenhaService.alterarSenha(request).subscribe(
      (mensagem) => {
        this.mensagem = this.mensagem;
      },
      (error) => {
        console.error('Erro na alteração de senha:', error);
        this.mensagem = 'Erro na alteração de senha';
      }
    );
  }
}
