import { MilitarService } from './../militar.service';
import { Component, OnInit } from '@angular/core';
import { Militar } from '../militar';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-update-militar',
  templateUrl: './update-militar.component.html',
  styleUrls: ['./update-militar.component.css']
})



export class UpdateMilitarComponent implements OnInit {

 id!: number;
 militar: Militar = new Militar();

  constructor(private militarService: MilitarService,
    private router: Router,
    private route: ActivatedRoute) {}



  ngOnInit(): void {

    this.id = this.route.snapshot.params['id']:
    this.militarService.getMilitarById(this.id).subscribe({
      next: (data: Militar) => {
        console.log(data)
        this.militar = data
      },
      error: (error: any) => console.log(error),
      complete: () => console.log("Data init comepleted!")
    })

}

onSubmit() {
  this.updateMilitar();
}
updateMilitar() {
  this.militarService.updateMilitar(this.id, this.militar)
  .subscribe({
    next: (data: any) => console.log(data),
    error: (error: any) => console.log(error),
    complete: () => this.goToMilitarList()
  })
}
goToMilitarList() {
  this.router.navigate(['/militarlist'])}
