import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Endereco } from '../endereco';
import { EnderecoService } from '../endereco.service';


@Component({
  selector: 'app-details-endereco',
  templateUrl: './details-endereco.component.html',
  styleUrls: ['./details-endereco.component.css']
})
export class DetailsEnderecoComponent implements OnInit {

  id!: number;
  endereco!: Endereco;
  constructor(private route: ActivatedRoute, private enderecoService: EnderecoService) { }

   ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];

     this.endereco = new Endereco();
     this.enderecoService.getEnderecoByid(this.id).subscribe( data => {
       this.endereco = data;
     })
 }
 }
