import { Component } from '@angular/core';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { Carros } from '../carros';
import { CarrosService } from '../carros.service';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';

@Component({
  selector: 'app-confirmar-interesse',
  templateUrl: './confirmar-interesse.component.html',
  styleUrls: ['./confirmar-interesse.component.css']
})
export class ConfirmarInteresseComponent {

  interessado: any;

  constructor(
    private router: Router,
    private carrosService: CarrosService,
    private clienteService: ClienteService
  ) {}

  ngOnInit(): void {
    this.interessado = history.state;
  }

  confirmarInteresse(): void {
    const clienteId = 1; // Substitua pelo ID do cliente atual

    const cliente = {
      id: clienteId,
      nome: 'Nome do Cliente', // Substitua pelo nome real do cliente
      telefone: 123456789, // Substitua pelo telefone real do cliente
      dataInteresse: new Date(),
      carroInteressado: this.interessado.carro,
    };

    // Chame o serviço para registrar a reserva
    this.clienteService.registrarReserva(cliente).subscribe(
      () => {
        // Remova o carro da lista de carros disponíveis
        this.carrosService.removerCarro(this.interessado.carro.id).subscribe(
          () => {
            // Redirecione para a página de carros reservados
            this.router.navigate(['/carros-reservados']);
          },
          (error) => {
            console.error('Erro ao remover o carro:', error);
          }
        );
      },
      (error) => {
        console.error('Erro ao registrar a reserva:', error);
      }
    );
  }
}
