import { Component, OnInit } from '@angular/core';

import { Router } from '@angular/router';
import { Endereco } from '../endereco';
import { EnderecoService } from '../endereco.service';

@Component({
  selector: 'app-endereco-list',
  templateUrl: './endereco-list.component.html',
  styleUrls: ['./endereco-list.component.css']
})
export class EnderecoListComponent implements OnInit {

 endereco: Endereco[] = [];

  constructor(private enderecoService: EnderecoService,
    private router: Router) { }


  ngOnInit(): void {
    this.getEnderecos();


   }

  private getEnderecos(){
    this.enderecoService.getEnderecosList().subscribe(data => {
      this.endereco = data;
    });
  }

 EnderecoDetails(id: number){
    this.router.navigate(['endereco-details', id] );
  }

  updateEndereco(id: number){
    this.router.navigate(['update-endereco', id] );
  }

  deleteEndereco(id: number){
    this.enderecoService.deleteEndereco(id).subscribe( data =>{
      console.log(data);
      this.getEnderecos();
    })
  }

}
