import { Component } from '@angular/core';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-lista-reservas',
  templateUrl: './lista-reservas.component.html',
  styleUrls: ['./lista-reservas.component.css']
})
export class ListaReservasComponent {

  cliente: Cliente[] = [];

  constructor(private clienteService: ClienteService) { }

  ngOnInit(): void {
    this.getClientes();


   }

  private getClientes(){
    this.clienteService.getClientesList().subscribe(data => {
      this.cliente = data;
    });
  }


}
<body class="body">
    <h3 class="text-center" class="cor"> Lista de Reservas</h3>

    <table class="table table-striped">
    <thead>
    <tr>
        <th>Cliente</th>
        <th>Telefone</th>
        <th>Data do Interesse</th>
        <th>Modelo</th>
        <th> Ações </th>

    </tr>

    </thead>

    <tbody>



        <tr *ngFor = "let row of cliente">

            <td> {{ row.nome }} </td>
            <td> {{ row.telefone }} </td>
            <td> {{ row.dataInteresse }} </td>
            <td> {{ row.carroInteressado }} </td>

            <td>
                <button class="btn btn-info" style="margin-left: 5px">Gerar Relatório PDF</button> 

                      </td>

        </tr>
    </tbody>

    </table>
    </body>
