import { Component, OnInit } from '@angular/core';
import { Usuario } from '../usuario';
import { UsuarioService } from '../usuario.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-usuario',
  templateUrl: './create-usuario.component.html',
  styleUrls: ['./create-usuario.component.css']
})
export class CreateUsuarioComponent implements OnInit {

 usuario: Usuario = new Usuario();

  constructor (private usuarioService: UsuarioService,
    private router: Router) { }


  ngOnInit(): void { }

    saveUsuario() {
      this.usuarioService.createUsuario(this.usuario).subscribe(data =>{

      })
      this.router.navigate(['/usuario-list'])
    }

    cancel(): void {
      this.router.navigate(['/usuario-list']);
    }

    onSubmit(){
      console.log(this.usuario);
      this.saveUsuario();
    }
}
<div class="col-md-6 offset-md-3">
    <h3 class="cor"> Cadastrar Usuarios</h3>
    <form (ngSubmit) = "onSubmit()">

        <div class="form-group">
            <label class="cor"> Login: </label>
            <input type="text" class ="form-control" id = "login"
                [(ngModel)] = "usuario.login" name = "login">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Senha: </label>
            <input type="text" class ="form-control" id = "senha"
                [(ngModel)] = "usuario.senha" name = "senha">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Nome: </label>
            <input type="text" class ="form-control" id = "nome"
                [(ngModel)] = "usuario.nome" name = "nome">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Ativo: </label>
            <input type="text" class ="form-control" id = "ativo"
                [(ngModel)] = "usuario.ativo" name = "ativo">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Data de Nascimento: </label>
            <input type="date" class ="form-control" id = "dataNascimento"
                [(ngModel)] = "usuario.dataNascimento" name = "dataNascimento">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Data de Cadastro: </label>
            <input type="date" class ="form-control" id = "dataCadastro"
                [(ngModel)] = "usuario.dataCadastro" name = "dataCadastro">
        </div>
        <div> </div>
        <button class = "btn btn-success" (click)="saveUsuario()">Cadastrar Usúario</button>



    </form>
    </div>
