import { UsuarioService } from '../usuario.service';
import { Component, OnInit } from '@angular/core';
import { Usuario } from '../usuario';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-update-usuario',
  templateUrl: './update-usuario.component.html',
  styleUrls: ['./update-usuario.component.css']
})



export class UpdateUsuarioComponent implements OnInit {

 id!: number;
 usuario: Usuario = new Usuario();

  constructor(private usuarioService: UsuarioService,
    private router: Router,
    private route: ActivatedRoute) {}



  ngOnInit(): void {

    this.id = this.route.snapshot.params['id'];
    this.usuarioService.getUsuarioByid(this.id).subscribe({
      next: (data: Usuario) => {
        console.log(data)
        this.usuario = data
      },
      error: (error: any) => console.log(error),
      complete: () => console.log("Data init comepleted!")
    })

}

onSubmit() {
  this.updateUsuario();
}
updateUsuario() {
  this.usuarioService.updateUsuario(this.id, this.usuario)
  .subscribe({
    next: (data: any) => console.log(data),
    error: (error: any) => console.log(error),
    complete: () => this.goToUsuarioList()
  })
}
goToUsuarioList() {
  this.router.navigate(['/usuario-list'])}
}
<div class="col-md-6 offset-md-3">
    <h3 class="cor"> Atualizar Militar </h3>
    <form (ngSubmit) = "onSubmit()">

        <div class="form-group">
            <label class="cor"> Nome: </label>
            <input type="text" class ="form-control" id = "nome"
                [(ngModel)] = "militar.nome" name = "nome">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Sobrenome: </label>
            <input type="text" class ="form-control" id = "sobrenome"
                [(ngModel)] = "militar.sobrenome" name = "sobrenome">
        </div>
        <div > </div>
        <div class="form-group">
            <label class="cor"> Tipo Sanguíneo: </label>
            <input type="text" class ="form-control" id = "tipoSanguineo"
                [(ngModel)] = "militar.tipoSanguineo" name = "tipoSanguineo">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Patente: </label>
            <input type="text" class ="form-control" id = "patente"
                [(ngModel)] = "militar.patente" name = "patente">
        </div>
        <div> </div>
        <button class = "btn btn-success" (click)="updateMilitar()">Atualizar Militar</button>



    </form>
    </div>
