import { Component } from '@angular/core';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { Carros } from '../carros';
import { CarrosService } from '../carros.service';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';

@Component({
  selector: 'app-confirmar-interesse',
  templateUrl: './confirmar-interesse.component.html',
  styleUrls: ['./confirmar-interesse.component.css']
})
export class ConfirmarInteresseComponent {

  cliente: Cliente = new Cliente();

  id!: number;
  carros!: Carros;
  constructor(private clienteService: ClienteService, private router: Router, private carrosService: CarrosService) { }

  ngOnInit(): void { }

  saveUsuario() {
    this.clienteService.createCliente(this.cliente).subscribe(data =>{

    })
    this.router.navigate(['/carros-list-client'])
  }

  cancel(): void {
    this.router.navigate(['/carros-list-client']);
  }

  onSubmit(){
    console.log(this.cliente);
    this.saveUsuario();
  }
}

