import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginData = { login: '', senha: '' };

  constructor(private http: HttpClient) {}

  onSubmit() {
    this.http.post('http://localhost:8080/pi/usuarios', this.loginData)
      .subscribe(
        (response: any) => {
          console.log('Login bem-sucedido: ' + response);
          // Redirecionar para a página após o login bem-sucedido
        },
        (error: any) => {
          console.error('Erro de login: ' + error);
          // Lidar com erros de login
        }
      );
  }
}


<div class="col-md-6 offset-md-3">

    <h3 class="cor"> Login </h3>
    <form (ngSubmit) = "onSubmit()">

        <div class="form-group">
            <label class="cor"> Login: </label>
            <input type="text" class ="form-control" id = "marca"
                [(ngModel)] = "loginData.login" name = "marca">
        </div>
        <div> </div>
        <div class="form-group">
            <label class="cor"> Senha: </label>
            <input type="text" class ="form-control" id = "modelo"
                [(ngModel)] = "loginData.senha" name = "modelo">
        </div>
        <button class = "btn btn-success"  >Acessar</button>



    </form>
    </div>
