import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Carros } from '../carros';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';
import { DadosCarrosService } from '../dados-carros.service';

@Component({
  selector: 'app-confirmar-interesse',
  templateUrl: './confirmar-interesse.component.html',
  styleUrls: ['./confirmar-interesse.component.css']
})

export class ConfirmarInteresseComponent implements OnInit {


  constructor(private dadosCarros: DadosCarrosService, private clienteService: ClienteService, private router: Router) {}

  cliente: Cliente = new Cliente();
  carroSelecionado: Carros | undefined;

  ngOnInit(): void {
    // Assine as alterações no carro selecionado
    this.dadosCarros.carroSelecionado$.subscribe(carro => {
      this.carroSelecionado = carro;
    });
  }

  onSubmit(): void {
    // Adicione lógica para confirmar reserva usando this.cliente e this.carroSelecionado

    // Verifique se há um cliente e um carro selecionado
    if (this.cliente && this.carroSelecionado) {
      // Atribua o carro ao cliente
      this.cliente.carroInteressado = this.carroSelecionado;

      // Chame o serviço para registrar a reserva
      this.clienteService.registrarReserva(this.cliente).subscribe(
        response => {
          console.log('Reserva registrada com sucesso!', response);
          // Adicione qualquer lógica de redirecionamento ou feedback adicional aqui
        },
        error => {
          console.error('Erro ao registrar a reserva', error);
          // Adicione qualquer lógica de feedback de erro aqui
        }
      );
    } else {
      console.error('Cliente ou carro selecionado ausentes');
      // Adicione qualquer lógica de feedback de erro aqui
    }
  }
}
